body.script:
const pacman = document.getElementById('pacman');
const svg = document.getElementById('pacman-svg');
const directions = [
    {dx: 5, dy: 0},
    {dx: -5, dy: 0},
    {dx: 0, dy: 5},
    {dx: 0, dy: -5}
];

function getRandomDirection() {
    return directions[Math.floor(Math.random() * directions.length)];
}

function movePacman() {
    const direction = getRandomDirection();
    let cx = parseFloat(pacman.getAttribute('cx')) + direction.dx;
    let cy = parseFloat(pacman.getAttribute('cy')) + direction.dy;

    if (cx < 0) cx = svg.clientWidth;
    if (cx > svg.clientWidth) cx = 0;
    if (cy < 0) cy = svg.clientHeight;
    if (cy > svg.clientHeight) cy = 0;

    pacman.setAttribute('cx', cx);
    pacman.setAttribute('cy', cy);
}

setInterval(movePacman, 200);
